suppressPackageStartupMessages(library(tidyverse));read_csv("analyse_sensibilite_inputs.csv", col_types=cols()) %>% select(-c(seed_sensib, sim_name, annee_sensib)) %>% mutate(sensibility_value = as.factor(sensibility_value)) %>% gather(Indicateur, Valeur, -sensibility_parameter, -sensibility_value) %>% ggplot() + aes(sensibility_value, Valeur, fill = sensibility_value) + geom_boxplot() + facet_wrap(sensibility_parameter~Indicateur, scales = "free", ncol = 10)
